// Check for manual override date parameter
const manualDate = $input.all()[0]?.json?.override_date;
const today = new Date().toISOString().split('T')[0];

return [{
  json: {
    current_date: today,
    override_date: manualDate || null,
    tracking_file_key: 'newsletter-automation-last-run.json'
  }
}];

// Parse tracking file or create default
let trackingData;
try {
  const inputData = $input.first();
  console.log('Input data keys:', Object.keys(inputData));

  // Try different data sources
  if (inputData.binary && inputData.binary.data) {
    console.log('Parsing from binary.data');
    const binaryContent = inputData.binary.data;
    if (typeof binaryContent === 'string') {
      trackingData = JSON.parse(binaryContent);
    } else if (binaryContent.data) {
      // Handle Base64 encoded JSON
      trackingData = JSON.parse(Buffer.from(binaryContent.data, 'base64').toString('utf8'));
    } else {
      trackingData = JSON.parse(binaryContent.toString());
    }
  } else if (inputData.json && typeof inputData.json === 'object' && inputData.json.last_processed_date !== undefined) {
    console.log('Using data from json field directly');
    trackingData = inputData.json;
  } else {
    throw new Error('No valid tracking data structure found');
  }
  console.log('Successfully parsed tracking data:', JSON.stringify(trackingData, null, 2));
} catch (error) {
  console.log('Error parsing tracking data:', error.message);
  console.log('Input data structure:', JSON.stringify($input.first(), null, 2));
  // First run - create default tracking
  trackingData = {
    last_successful_run: null,
    last_processed_date: null,
    files_processed: 0,
    workflow_version: "1.0"
  };
}

const currentDate = $('Initialize Date Processing').first().json.current_date;
const overrideDate = $('Initialize Date Processing').first().json.override_date;

// Calculate start date
let startDate;
if (overrideDate) {
  startDate = overrideDate;
} else if (trackingData.last_processed_date) {
  // Start from day after last processed
  const lastDate = new Date(trackingData.last_processed_date);
  lastDate.setDate(lastDate.getDate() + 1);
  const calculatedStart = lastDate.toISOString().split('T')[0];

  // If calculated start date is in the future, process today instead
  if (calculatedStart > currentDate) {
    console.log(`Calculated start date ${calculatedStart} is in future, using current date ${currentDate}`);
    startDate = currentDate;
  } else {
    startDate = calculatedStart;
  }
} else {
  // First run - start from today
  startDate = currentDate;
}

// Generate date prefixes to scan
const datePrefixes = [];
const start = new Date(startDate);
const end = new Date(currentDate);

for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
  datePrefixes.push(d.toISOString().split('T')[0]);
}

// Debug logging
console.log('Date Range Calculation:');
console.log(`  Last processed date: ${trackingData.last_processed_date}`);
console.log(`  Current date: ${currentDate}`);
console.log(`  Calculated start date: ${startDate}`);
console.log(`  Date prefixes to process: ${JSON.stringify(datePrefixes)}`);

return [{
  json: {
    tracking_data: trackingData,
    current_date: currentDate,
    start_date: startDate,
    date_prefixes: datePrefixes,
    dates_to_process: datePrefixes.length
  }
}];

// Filter for .md files and extract metadata from bucket search results
const items = $input.all();
const mdFiles = [];
const targetDates = $('Calculate Date Range').first().json.date_prefixes;

console.log(`Filter & Parse: Looking for files with target dates: ${targetDates.join(', ')}`);
console.log(`Filter & Parse: Processing ${items.length} S3 search result items`);

for (const item of items) {
  // Handle different possible data structures from bucket search
  let files = [];

  // Check various possible response formats
  if (item.json.Contents) {
    files = item.json.Contents;
  } else if (item.json.objects) {
    files = item.json.objects;
  } else if (item.json.files) {
    files = item.json.files;
  } else if (Array.isArray(item.json)) {
    files = item.json;
  } else if (item.json.Key) {
    // Single file object
    files = [item.json];
  }

  console.log(`Processing ${files.length} files from S3 search result`);

  for (const file of files) {
    // Get the key/name from various possible fields
    const key = file.Key || file.key || file.name || file.Key;

    if (key && key.endsWith('.md')) {
      // Parse key format: YYYY-MM-DD/title.source.md
      const parts = key.split('/');
      if (parts.length === 2) {
        const date = parts[0];
        const filename = parts[1];
        const nameParts = filename.replace('.md', '').split('.');

        if (nameParts.length >= 2) {
          const source = nameParts[nameParts.length - 1];
          const titleSlug = nameParts.slice(0, -1).join('.');

          mdFiles.push({
            key: key,
            date: date,
            title_slug: titleSlug,
            source: source,
            size: file.Size || file.size || 0,
            last_modified: file.LastModified || file.lastModified || new Date().toISOString()
          });
        }
      }
    }
  }
}

// Sort by date and source for consistent processing
mdFiles.sort((a, b) => {
  if (a.date !== b.date) return a.date.localeCompare(b.date);
  return a.source.localeCompare(b.source);
});

console.log(`Filter & Parse: Found ${mdFiles.length} .md files total`);
console.log(`Filter & Parse: Dates found: ${[...new Set(mdFiles.map(f => f.date))].join(', ')}`);
console.log(`Filter & Parse: Files matching target dates: ${mdFiles.filter(f => targetDates.includes(f.date)).length}`);

return mdFiles.map(file => ({ json: file }));

// Filter markdown files to only include dates within the calculated range and deduplicate
const mdFiles = $input.all();
const dateRange = $('Calculate Date Range').first().json.date_prefixes;

const filteredFiles = [];
const seenFiles = new Set();

for (const fileItem of mdFiles) {
  const file = fileItem.json;

  // Check if the file's date is within our target date range
  if (dateRange.includes(file.date)) {
    // Use file key as unique identifier to prevent duplicates
    const fileKey = file.key;
    if (!seenFiles.has(fileKey)) {
      seenFiles.add(fileKey);
      filteredFiles.push(file);
    }
  }
}

// Log filtering results for debugging
console.log(`Date range filter: ${dateRange.length} target dates, ${mdFiles.length} total files, ${filteredFiles.length} files after filtering and deduplication`);
console.log(`Target dates: ${dateRange.join(', ')}`);
console.log(`Filtered files by date: ${filteredFiles.map(f => f.date).join(', ')}`);
console.log(`Unique file keys: ${filteredFiles.map(f => f.key).join(', ')}`);

return filteredFiles.map(file => ({ json: file }));

// Extract markdown content, apply enhanced content cleaning, and combine with metadata
const items = $input.all();
const results = [];

// STAGE 1 ENHANCED CONTENT CLEANING FUNCTIONS

// 1. Remove advertisements, sponsorships, and promotional content
function removeAdvertisements(content) {
  return content
    // Remove "PRESENTED BY" and "FROM OUR PARTNERS" sections (comprehensive)
    .replace(/#{1,6}\s*\**(?:PRESENTED BY|FROM OUR PARTNERS).*?\**/gim, '')
    .replace(/\*\*(?:PRESENTED BY|FROM OUR PARTNERS).*?\*\*/gi, '')
    .replace(/#{1,6}\s*FROM OUR PARTNERS[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove sponsor blocks and acknowledgments
    .replace(/sponsored by.*?(?=\n|$)/gi, '')
    .replace(/in partnership with.*?(?=\n|$)/gi, '')
    .replace(/\*\*sponsored.*?\*\*/gi, '')
    .replace(/\*\*featured.*?\*\*/gi, '')
    // Remove subscription and promotional sections
    .replace(/\[.*?browse.*?tools.*?\]/gi, '')
    .replace(/\[.*?browse.*?courses.*?\]/gi, '')
    .replace(/\[.*?advertise.*?\]/gi, '')
    .replace(/\[.*?sponsorship.*?options.*?\]/gi, '')
    .replace(/\[.*?book a call.*?\]/gi, '')
    // Remove promotional calls-to-action
    .replace(/click here to.*?(?=\n|$)/gi, '')
    .replace(/sign up.*?(?=\n|$)/gi, '')
    .replace(/get started for free.*?(?=\n|$)/gi, '')
    .replace(/register today.*?(?=\n|$)/gi, '')
    .replace(/claim.*?credits.*?(?=\n|$)/gi, '')
    // Remove weekly spotlight and promotional sections
    .replace(/#{1,6}\s*Weekly Spotlight[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*.*?Spotlight[\s\S]*?(?=#{1,6}|$)/gi, '');
}

// 2. Remove images and videos
function removeMediaContent(content) {
  return content
    // Remove markdown images
    .replace(/!\[.*?\]\(.*?\)/g, '')
    // Remove HTML img tags
    .replace(/<img[^>]*>/gi, '')
    // Remove video embeds
    .replace(/<video[^>]*>.*?<\/video>/gis, '')
    .replace(/\[.*?video.*?\]/gi, '')
    .replace(/watch.*?here.*?\[.*?\]/gi, '')
    // Remove image source references and captions
    .replace(/image source:.*?(?=\n|$)/gi, '')
    .replace(/source:.*?(?=\n|$)/gi, '');
}

// 3. Clean URLs (remove UTM parameters but preserve important links)
function cleanUrls(content) {
  return content
    // Clean URLs in parentheses format (link) - remove everything after ?
    .replace(/\((https?:\/\/[^\s\)]+)\?[^\)]*\)/g, '($1)')
    // Clean URLs in markdown link format [text](url) - remove everything after ?
    .replace(/\[([^\]]+)\]\((https?:\/\/[^\s\)]+)\?[^\)]*\)/g, '[$1]($2)')
    // Clean standalone URLs with UTM parameters
    .replace(/(https?:\/\/[^\s]+)\?utm_[^\s]*/g, '$1')
    // Clean any other tracking parameters
    .replace(/(https?:\/\/[^\s]+)\?[^\s]*(?:utm_|ref=|track=|source=)[^\s]*/g, '$1')
    // Remove empty markdown links
    .replace(/\[\]\([^\)]*\)/g, '')
    // Remove promotional/tracking URLs but preserve content URLs
    .replace(/\[.*?(?:advertise|sponsor|promote|track|analytics).*?\]\([^\)]+\)/gi, '')
    .replace(/https?:\/\/[^\s]*(?:advertise|sponsor|promote|track|analytics|utm_)[^\s]*/gi, '');
}

// 4. Remove engagement content, polls, and interface elements
function removeEngagementContent(content) {
  return content
    // Remove comprehensive poll patterns
    .replace(/what did you think.*?\?.*?(?=\n|$)/gi, '')
    .replace(/how.*?did.*?we.*?do.*?\?.*?(?=\n|$)/gi, '')
    .replace(/rate this.*?(?=\n|$)/gi, '')
    .replace(/feedback.*?\?.*?(?=\n|$)/gi, '')
    // Remove poll options with star ratings
    .replace(/^\s*-\s*[^\n]*‚≠ê[^\n]*$/gim, '')
    .replace(/^\s*-\s*[^\n]*üß†[^\n]*$/gim, '')
    .replace(/amazing.*?loved it.*?‚≠ê.*?(?=\n|$)/gi, '')
    .replace(/terrible.*?‚≠ê.*?(?=\n|$)/gi, '')
    .replace(/nailed it.*?‚≠ê.*?(?=\n|$)/gi, '')
    // Remove login/subscribe prompts
    .replace(/login.*?or.*?subscribe.*?to participate.*?(?=\n|$)/gi, '')
    .replace(/\[login\].*?\[subscribe\]/gi, '')
    // Remove social engagement prompts
    .replace(/share this.*?(?=\n|$)/gi, '')
    .replace(/follow us.*?(?=\n|$)/gi, '')
    // Remove "Keep reading" sections
    .replace(/#{1,6}\s*Keep reading[\s\S]*?$/gi, '')
    .replace(/keep reading[\s\S]*?$/gi, '');
}

// 5. Remove ancillary content, navigation, and newsletter meta-content
function removeAncillaryContent(content) {
  return content
    // Remove navigation elements
    .replace(/view more.*?(?=\n|$)/gi, '')
    .replace(/twitter widget iframe/gi, '')
    .replace(/read more.*?(?=\n|$)/gi, '')
    // Remove comprehensive "In this issue" patterns
    .replace(/in this issue[:.]*[\s\S]*?(?=\n#|$)/gi, '')
    .replace(/^in this issue.*?(?=\n|$)/gim, '')
    .replace(/^\*\*in today.*?ai.*?\*\*[\s\S]*?(?=\n#|$)/gim, '')
    // Remove "What's trending" and similar sections
    .replace(/#{1,6}\s*What's trending[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*.*?trending.*?social.*?headlines[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove repeated headlines (pattern: "- Title # Title")
    .replace(/^-\s*(.+?)\s*#\s*\1\s*$/gim, '# $1')
    // Enhanced duplicate title removal - exact duplicates on consecutive lines
    .replace(/^(.{10,})\s*\n+\1\s*$/gim, '$1')
    // Remove duplicate titles with hash patterns (# Title\nTitle)
    .replace(/^#\s*(.+?)\s*\n+\1\s*$/gim, '# $1')
    // Remove table structures used for layout (non-data tables)
    .replace(/\|\s*\|\s*\|[\s\S]*?\|\s*$/gm, '')
    .replace(/^\s*\|.*?\|\s*$/gm, '')
    // Remove footer elements
    .replace(/unsubscribe.*?(?=\n|$)/gi, '')
    .replace(/manage preferences.*?(?=\n|$)/gi, '')
    .replace(/until next time.*?(?=\n|$)/gi, '')
    .replace(/see you.*?soon.*?(?=\n|$)/gi, '');
}

// 6. Remove author information and publication meta-data
function removeAuthorInfo(content) {
  return content
    // Remove bylines and author attribution
    .replace(/^by\s+.*?(?=\n|$)/gim, '')
    .replace(/author:?\s*.*?(?=\n|$)/gi, '')
    // Remove author images and social links
    .replace(/\[.*?author.*?\]/gi, '')
    .replace(/@\w+/g, '')  // Remove @ mentions
    // Remove publication dates with author names
    .replace(/.*?\d{4}.*?\/.*?(?=\n|$)/g, '')
    // Remove team signatures and author attributions
    .replace(/^.*?team$|team.*?(?=\n|$)/gim, '')
    .replace(/zain.*?kahn.*?(?=\n|$)/gi, '')
    .replace(/grant.*?harvey.*?(?=\n|$)/gi, '')
    .replace(/zach.*?mink.*?(?=\n|$)/gi, '')
    // Remove "humans behind" signatures
    .replace(/the humans behind.*?(?=\n|$)/gi, '')
    .replace(/see you.*?(?=\n|$)/gi, '');
}

// 7. Remove newsletter-specific patterns
function removeNewsletterSpecificPatterns(content) {
  return content
    // Remove tool recommendation sections
    .replace(/#{1,6}\s*Hot Tools[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*.*?tools.*?releases[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*Deep Dives?[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*Trending AI Tools[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove "Whenever you're ready" sections
    .replace(/whenever you.*?ready.*?(?=\n|$)/gi, '')
    .replace(/when.*?ready.*?take.*?next.*?step.*?(?=\n|$)/gi, '')
    // Remove community and promotional sections
    .replace(/#{1,6}\s*Community[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*.*?workflows?[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove trivia and game sections
    .replace(/#{1,6}\s*.*?Trivia[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*.*?Commentary[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove productivity sections with tool lists
    .replace(/#{1,6}\s*Productivity[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*.*?AI Tools.*?Productivity[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove academic/course promotional content
    .replace(/#{1,6}\s*.*?Academy[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove meme and social content
    .replace(/meme of the day.*?(?=\n|$)/gi, '')
    .replace(/#{1,6}\s*.*?meme.*?(?=#{1,6}|$)/gi, '')
    // Remove "Quick Hits" and "Everything else" sections
    .replace(/#{1,6}\s*Quick Hits[\s\S]*?(?=#{1,6}|$)/gi, '')
    .replace(/#{1,6}\s*Everything else.*?today[\s\S]*?(?=#{1,6}|$)/gi, '')
    // Remove "Highlights" and event sections
    .replace(/#{1,6}\s*Highlights.*?News.*?Guides.*?Events[\s\S]*?(?=#{1,6}|$)/gi, '');
}

// 8. Remove all emojis from content
function removeEmojis(content) {
  return content
    // Remove all emoji characters (comprehensive Unicode ranges)
    .replace(/[\u{1F600}-\u{1F64F}]/gu, '') // Emoticons
    .replace(/[\u{1F300}-\u{1F5FF}]/gu, '') // Misc Symbols and Pictographs
    .replace(/[\u{1F680}-\u{1F6FF}]/gu, '') // Transport and Map
    .replace(/[\u{1F1E0}-\u{1F1FF}]/gu, '') // Regional country flags
    .replace(/[\u{2600}-\u{26FF}]/gu, '')   // Misc symbols
    .replace(/[\u{2700}-\u{27BF}]/gu, '')   // Dingbats
    .replace(/[\u{FE00}-\u{FE0F}]/gu, '')   // Variation Selectors
    .replace(/[\u{1F900}-\u{1F9FF}]/gu, '') // Supplemental Symbols and Pictographs
    .replace(/[\u{1F018}-\u{1F270}]/gu, '') // Various asian characters
    .replace(/[\u{238C}\u{2744}\u{2764}]/gu, '') // Misc additional emojis
    // Remove common emoji patterns
    .replace(/‚≠ê+/g, '')
    .replace(/üî•+/g, '')
    .replace(/üõ†Ô∏è+/g, '')
    .replace(/üß†+/g, '')
    .replace(/üìß+/g, '')
    .replace(/üöó+/g, '')
    .replace(/üé§+/g, '')
    .replace(/üí´+/g, '')
    .replace(/üåê+/g, '')
    .replace(/üì∏+/g, '')
    .replace(/üèóÔ∏è+/g, '')
    .replace(/üíª+/g, '')
    .replace(/üéØ+/g, '')
    .replace(/‚ö°+/g, '')
    .replace(/‚ú®+/g, '')
    .replace(/üëØ+/g, '')
    .replace(/üìÑ+/g, '')
    .replace(/üñºÔ∏è+/g, '')
    .replace(/üìú+/g, '')
    .replace(/üó£Ô∏è+/g, '');
}

// 9. Clean excessive whitespace and formatting
function normalizeWhitespace(content) {
  return content
    .replace(/\n\s*\n\s*\n/g, '\n\n')  // Collapse multiple line breaks
    .replace(/^\s+|\s+$/gm, '')         // Trim lines
    .replace(/\s+/g, ' ')              // Normalize spaces
    .trim();
}

// MAIN PROCESSING LOOP
for (let i = 0; i < items.length; i++) {
  const item = items[i];
  let content = '';

  // The S3 download puts content in binary.data as a JSON object
  if (item.binary && item.binary.data) {
    try {
      // Parse the JSON object from binary data
      let binaryContent;
      if (typeof item.binary.data === 'string') {
        binaryContent = JSON.parse(item.binary.data);
      } else {
        binaryContent = item.binary.data;
      }

      // Extract the Base64 data and decode it
      if (binaryContent && binaryContent.data) {
        content = Buffer.from(binaryContent.data, 'base64').toString('utf8');
      }
    } catch (error) {
      // Fallback to string content if JSON parsing fails
      if (Buffer.isBuffer(item.binary.data)) {
        content = item.binary.data.toString('utf8');
      } else if (typeof item.binary.data === 'string') {
        content = item.binary.data;
      }
    }
  }

  // APPLY ENHANCED CONTENT CLEANING PIPELINE
  const originalLength = content.length;

  content = removeAdvertisements(content);
  content = removeMediaContent(content);
  content = cleanUrls(content);
  content = removeEngagementContent(content);
  content = removeAncillaryContent(content);
  content = removeAuthorInfo(content);
  content = removeNewsletterSpecificPatterns(content);
  content = removeEmojis(content);
  content = normalizeWhitespace(content);

  const cleanedLength = content.length;
  const reductionPercentage = originalLength > 0 ? ((originalLength - cleanedLength) / originalLength * 100).toFixed(1) : 0;

  const metadata = item.json || {};

  // Clean up title from slug
  const title = metadata.title_slug
    ? metadata.title_slug.split('-').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ')
    : `File ${i + 1}`;

  results.push({
    ...metadata,
    title: title,
    content: content,
    content_length: cleanedLength,
    original_length: originalLength,
    cleaning_reduction: `${reductionPercentage}%`,
    word_count: content ? content.split(/\s+/).filter(word => word.length > 0).length : 0
  });
}

return results.map(result => ({ json: result }));

// Combine all articles into a structured markdown newsletter with single-date focus
const articles = $input.first().json.data;

// STAGE 1.2: SINGLE-DATE CONTENT PROCESSING
// Determine target date (most recent or current date)
const today = new Date().toISOString().split('T')[0];

// Group articles by date and identify target date
const articlesByDate = {};
for (const article of articles) {
  if (!articlesByDate[article.date]) {
    articlesByDate[article.date] = [];
  }
  articlesByDate[article.date].push(article);
}

const availableDates = Object.keys(articlesByDate).sort();
const targetDate = availableDates.includes(today) ? today : availableDates[availableDates.length - 1];

// ONLY PROCESS ARTICLES FROM TARGET DATE
const targetArticles = articlesByDate[targetDate] || [];

// Build combined markdown for single date only
let combinedMarkdown = '';

// Header - always single date
combinedMarkdown += `# Daily Newsletter - ${targetDate}\n\n`;

// Add processing note if date mismatch
if (targetDate !== today) {
  combinedMarkdown += `*Note: Processing newsletter for ${targetDate} (latest available content)*\n\n`;
}

// Content for target date only
if (targetArticles.length > 0) {
  combinedMarkdown += `## ${targetDate}\n\n`;

  for (const article of targetArticles) {
    // Format source name nicely
    const sourceName = article.source
      .split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');

    combinedMarkdown += `### ${article.title} (${sourceName})\n\n`;
    combinedMarkdown += `${article.content}\n\n`;

    // Add cleaning statistics as comment for debugging
    if (article.cleaning_reduction) {
      combinedMarkdown += `<!-- Content cleaned: ${article.cleaning_reduction} reduction from ${article.original_length} chars -->\n\n`;
    }

    combinedMarkdown += `---\n\n`;
  }
} else {
  combinedMarkdown += `No articles available for ${targetDate}.\n\n`;
}

// Generate statistics for target date only
const totalArticles = targetArticles.length;
const totalWords = targetArticles.reduce((sum, article) => sum + article.word_count, 0);
const totalOriginalChars = targetArticles.reduce((sum, article) => sum + (article.original_length || 0), 0);
const totalCleanedChars = targetArticles.reduce((sum, article) => sum + article.content_length, 0);
const overallReduction = totalOriginalChars > 0 ? ((totalOriginalChars - totalCleanedChars) / totalOriginalChars * 100).toFixed(1) : 0;
const sources = [...new Set(targetArticles.map(a => a.source))];

// Include all available dates for reference
const dateInfo = {
  target_date: targetDate,
  available_dates: availableDates,
  date_selection_reason: targetDate === today ? 'current_date' : 'latest_available',
  total_dates_available: availableDates.length,
  skipped_dates: availableDates.filter(date => date !== targetDate)
};

// Ensure we have content to save - prevent Buffer undefined errors
if (!combinedMarkdown || combinedMarkdown.trim().length === 0) {
  combinedMarkdown = `# Daily Newsletter - ${targetDate}\n\nNo content available for processing.\n\nProcessing Date: ${new Date().toISOString()}\nAvailable Dates: ${availableDates.join(', ')}\nTarget Date: ${targetDate}\n\nThis may indicate an issue with data processing or no articles were found for the target date.`;
}

return [{
  json: {
    newsletter_content: combinedMarkdown,
    date_processing: dateInfo,
    content_cleaning_stats: {
      total_articles: totalArticles,
      total_words: totalWords,
      original_characters: totalOriginalChars,
      cleaned_characters: totalCleanedChars,
      overall_reduction: `${overallReduction}%`,
      sources_processed: sources
    },
    statistics: {
      total_articles: totalArticles,
      total_words: totalWords,
      date_range: { start: targetDate, end: targetDate },
      sources_processed: sources,
      processing_date: new Date().toISOString(),
      content_cleaning_enabled: true
    }
  }
}];

// Update tracking file with latest run information
const statistics = $input.first().json.statistics;
const originalTracking = $('Calculate Date Range').first().json.tracking_data;

const updatedTracking = {
  last_successful_run: new Date().toISOString(),
  last_processed_date: statistics.date_range.end,
  files_processed: statistics.total_articles,
  workflow_version: "1.0",
  last_run_statistics: statistics
};

return [{
  json: {
    tracking_update: updatedTracking,
    newsletter_content: $input.first().json.newsletter_content,
    statistics: statistics
  }
}];
